import React, { useState } from 'react';
// import { makeStyles } from '@material-ui/core/styles';

import { useHistory, Link } from 'react-router-dom';
// import axios from 'axios';

const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      width: '45ch',
    },
  },
}));

const Form = ({ setLogoutUser }) => {
  const classes = useStyles();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  let history = useHistory();

  const login = (e) => {
    e.preventDefault();
    axios
      .post('http://localhost:5000/api/auth/login', {
        email,
        password,
      })
      .then((response) => {
        console.log('response', response);
        localStorage.setItem(
          'login',
          JSON.stringify({
            userLogin: true,
            token: response.data.access_token,
          })
        );
        setError('');
        setEmail('');
        setPassword('');
        // setLogoutUser(false);
        history.push('/');
      });
    // .catch((error) => setError(error.response.data.message));
  };

  return (
    <form autoComplete='off' onSubmit={login} id='login'>

      <section className='flex justify-center items-center h-screen bg-gray-100'>
        <div className='max-w-md w-full bg-white rounded p-6 space-y-4'>
          <div className='mb-4'>
            <p className='text-gray-600'>Sign In</p>
            <h2 className='text-xl font-bold'>Join our community</h2>
          </div>
          <div>
            <input
              className='w-full p-4 text-sm bg-gray-50 focus:outline-none border border-gray-200 rounded text-gray-600'
              id="username"
          label="Username"
          type="text"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div>
            <input
              className='w-full p-4 text-sm bg-gray-50 focus:outline-none border border-gray-200 rounded text-gray-600'
              id="password"
              label="Password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div>
            <button  type="submit" className='w-full py-4 bg-blue-600 hover:bg-blue-700 rounded text-sm font-bold text-gray-50 transition duration-200'>
              Sign In
            </button>
          </div>
          <div className='flex items-center justify-between'>
            <div className='flex flex-row items-center'>
              <input
                type='checkbox'
                className='focus:ring-blue-500 h-4 w-4 text-blue-600 border-gray-300 rounded'
              />
              <label
                className='ml-2 text-sm font-normal text-gray-600'
              >
                Remember me
              </label>
            </div>
            <div>
              <a className='text-sm text-blue-600 hover:underline' href='#'>
                Forgot password?
              </a>
            </div>
          </div>
        </div>
      </section>
    </form>
  );
};

export default Form;
